import React from 'react';
import { ReactTyped } from "react-typed";

const Hero = () => {
    return (
        <div className='text-white'>
            <div className='max-w-[800px] mt-[-96px] w-full h-screen mx-auto text-center flex flex-col justify-center'>
                <p className='text-yellow-500 font-bold p-2' style={{ textShadow: '0 0 5px #ffcc00, 0 0 10px #ffcc00, 0 0 15px #ffcc00, 0 0 20px #ffcc00, 0 0 30px #ffcc00, 0 0 40px #ffcc00, 0 0 55px #ffcc00, 0 0 75px #ffcc00' }}>LabelChain Rewards</p>
                <h1 className='md:test-7xl sm:text-6xl text-4xl font-bold md:py-6'>Verified for Accuracy</h1>
                <div className='flex justify-center items-center'>
                    <p className='md:text-5xl sm:text-4xl text-xl font-bold py-4'>Fast, flexible financing for</p>
                    <ReactTyped 
                        className='md:text-5xl sm:text-4xl text-xl font-bold pl-2'
                        strings={['BTB', 'BTC', 'SASS']} 
                        typeSpeed={120} 
                        backSpeed={140} 
                        loop 
                    />
                </div>
                <p className='md:text-2xl text-xl font-bold text-gray-500'>Have your data labeled or complete available labeling tasks to earn bitcoin.</p>
                <button className='bg-blue-500 hover:bg-blue-600 w-[200px] rounded-md font-medium my-6 mx-auto py-3 text-white'>Get Started</button>
            </div>
        </div>
    );
};

export default Hero;
