import React from 'react'
import { ReactTyped } from "react-typed";

const Hero = () => {
    return (
        <div className='text-white'>
            <div className = 'max-w-[800px] mt-[-96px] w-full h-screen mx-auto text-center flex flex-col justify-center'>
                <p className = 'text-[#00df9a] font-bold p-2'>DECENTRALIZED MARKETPLACE FOR DATA LABELING</p>
                <h1 className='md:test-7xl sm:text-6xl text-4xl font-bold md:py-6'>Reliable Accuracy</h1>
                <div className='flex justify-center items-center'>
                    <p className='md:text-5xl sm:text-4xl text-xl font-bold py-4'>Fast, flexible financing</p>
                    <ReactTyped 
                    className='md:text-5xl sm:text-4xl text-xl font-bold pl-2'
                        strings={['BTB', 'BTC', 'SASS']} 
                        typeSpeed={120} 
                        backSpeed={140} 
                        loop />
                </div>
                <p className='md:text-2xl text-xl font-bold text-gray-500'>Have your data labeled or complete available labeling tasks to earn bitcoin.</p>
            </div>
        </div>
    )
}

export default Hero
